import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Typography,
  TextField,
  Button,
  CircularProgress
} from '@mui/material';
import { ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from '@mui/x-charts';

function App() {
  const [data, setData] = useState([]);
  const [parameter, setParameter] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchData = () => {
    setLoading(true);
    axios.get(`http://localhost:5000/data?parameter=${parameter}`)
      .then(response => {
        const fetchedData = response.data.map(item => ({
          ...item,
          timestamp: new Date(item.timestamp).getTime(), // Convert timestamp to a numerical value for the chart
        }));
        setData(fetchedData);
        setLoading(false);
      })
      .catch(error => {
        console.error('There was an error fetching the data!', error);
        setLoading(false);
      });
  };

  console.log("AAAAAAAAA")
  console.log(data)

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>
        Time-Series Data
      </Typography>
      <TextField
        label="Parameter"
        variant="outlined"
        value={parameter}
        onChange={(e) => setParameter(e.target.value)}
      />
      <Button variant="contained" color="primary" onClick={fetchData}>
        Fetch Data
      </Button>
      {loading ? (
        <CircularProgress />
      ) : (
          <ScatterChart
            width={800}
            height={400}
            margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
          >
            <Scatter name="Values" data={data} fill="#8884d8" />
          </ScatterChart>
      )}
    </Container>
  );
}

export default App;
























