import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  ScatterChart
} from '@mui/material';


function App() {
  const [data, setData] = useState([]);
  const [parameter, setParameter] = useState('');

  const fetchData = () => {
    axios.get(`http://localhost:5000/data?parameter=${parameter}`)
      .then(response => {
        const fetchedData = response.data.map(item => ({
          ...item,
          timestamp: new Date(item.timestamp).getTime(), // Convert timestamp to a numerical value for the chart
        }));
        setData(fetchedData);
      })
      .catch(error => {
        console.error('There was an error fetching the data!', error);
      });
  };

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>
        Time-Series Data
      </Typography>
      <TextField
        label="Parameter"
        variant="outlined"
        value={parameter}
        onChange={(e) => setParameter(e.target.value)}
      />
      <Button variant="contained" color="primary" onClick={fetchData}>
        Fetch Data
      </Button>
      <ResponsiveContainer width="100%" height={400} style={{ marginTop: '20px' }}>
        <ScatterChart
          width={800}
          height={400}
          margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
        >
          <CartesianGrid />
          <XAxis
            dataKey="timestamp"
            name="Time"
            domain={['auto', 'auto']}
            tickFormatter={(unixTime) => new Date(unixTime).toLocaleString()}
          />
          <YAxis dataKey="value" name="Value" />
          <Tooltip cursor={{ strokeDasharray: '3 3' }} />
          <Legend />
          <Scatter name="Values" data={data} fill="#8884d8" />
        </ScatterChart>
      </ResponsiveContainer>
    </Container>
  );
}

export default App;
